mapper.select:
select count(*)
from ${prefix}CRB_RU_JOB J
where J.SIMULATION_INSTANCE_ID_ = #{id}

  	${limitBefore}
    select 
    	RES.* ${limitBetween}    		
    from ${prefix}CRB_RU_JOB RES    
    	LEFT OUTER JOIN ${prefix}CRB_RU_SIMULATION SI ON SI.ID_ = RES.SIMULATION_INSTANCE_ID_
    where (RES.RETRIES_ > 0)
      and (RES.DUEDATE_ is null or RES.DUEDATE_ < #{parameter, jdbcType=TIMESTAMP})
      and (RES.LOCK_OWNER_ is null or RES.LOCK_EXP_TIME_ < #{parameter, jdbcType=TIMESTAMP})
	  and (
	  		SI.SUSPENSION_STATE_ = 1
      )  
    ${limitAfter}

${limitBefore}
select RES.* ${limitBetween} 
from ${prefix}ACT_RU_JOB RES
  LEFT OUTER JOIN ${prefix}ACT_RU_EXECUTION PI ON PI.ID_ = RES.PROCESS_INSTANCE_ID_
where (RETRIES_ > 0)
  and (DUEDATE_ is null or DUEDATE_ <= #{parameter, jdbcType=TIMESTAMP})
  and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ <= #{parameter, jdbcType=TIMESTAMP})
  and (
      (RES.EXECUTION_ID_ is null)
    or 
    (PI.SUSPENSION_STATE_ = 1)     
  )  
${limitAfter}

	${limitBefore}
  select RES.* ${limitBetween} 
  from ${prefix}ACT_RU_JOB RES    
  where (RETRIES_ > 0)
    and (DUEDATE_ is null or DUEDATE_ < #{parameter.now, jdbcType=TIMESTAMP})
    and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ < #{parameter.now, jdbcType=TIMESTAMP})
    and (EXCLUSIVE_ = TRUE)
    and (PROCESS_INSTANCE_ID_ = #{parameter.pid})  
  ${limitAfter}

${limitBefore}
select RES.* ${limitBetween} 
from ${prefix}ACT_RU_JOB RES
where (RETRIES_ > 0)
  and (DUEDATE_ is null or DUEDATE_ <= #{parameter.now, jdbcType=TIMESTAMP})
  and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ <= #{parameter.now, jdbcType=TIMESTAMP})
  and (EXCLUSIVE_ = TRUE)
  and (PROCESS_INSTANCE_ID_ = #{parameter.pid})
${limitAfter}

	${limitBefore}
  select RES.* ${limitBetween} 
  from ${prefix}ACT_RU_JOB RES  
  where (RETRIES_ > 0)
    and (DUEDATE_ is null or DUEDATE_ < #{parameter.now, jdbcType=TIMESTAMP})
    and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ < #{parameter.now, jdbcType=TIMESTAMP})
    and (EXCLUSIVE_ = 1)
    and (PROCESS_INSTANCE_ID_ = #{parameter.pid})  
  ${limitAfter}

select * from ${prefix}ACT_RU_JOB
where HANDLER_TYPE_ = #{parameter.handlerType}
and HANDLER_CFG_ =  #{parameter.handlerConfiguration}

mapper.sql:
from ${prefix}ACT_RU_JOB RES

    LEFT OUTER JOIN ${prefix}ACT_RU_EXECUTION PI ON PI.ID_ = RES.PROCESS_INSTANCE_ID_



    RES.ID_ = #{id}


    and RES.PROCESS_INSTANCE_ID_ = #{processInstanceId}


    and RES.EXECUTION_ID_ = #{executionId}


    and RES.PROC_DEF_ID_ = #{processDefinitionId}


    and RES.RETRIES_ > 0


    and (RES.RETRIES_ > 0)
    and (RES.DUEDATE_ is null or RES.DUEDATE_ <= #{now, jdbcType=TIMESTAMP})
    and (
        (RES.EXECUTION_ID_ is null)
        or 
        (PI.SUSPENSION_STATE_ = 1)     
  )


    and RES.TYPE_ = 'timer'


    and RES.TYPE_ = 'message'


    and RES.DUEDATE_ > #{duedateHigherThen}


    and RES.DUEDATE_ < #{duedateLowerThen}


    and RES.DUEDATE_ >= #{duedateHigherThenOrEqual}


    and RES.DUEDATE_ <= #{duedateLowerThenOrEqual}


  	and (RES.EXCEPTION_MSG_ is not null or RES.EXCEPTION_STACK_ID_ is not null)


  	and RES.EXCEPTION_MSG_ = #{exceptionMessage}

mapper.update:
update ${prefix}ACT_RU_JOB

   REV_ =  #{revisionNext, jdbcType=INTEGER},
   LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP},
   LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR},
   RETRIES_ = #{retries, jdbcType=INTEGER},
   EXCEPTION_STACK_ID_ = #{exceptionByteArrayId, jdbcType=VARCHAR},
   EXCEPTION_MSG_ = #{exceptionMessage, jdbcType=VARCHAR},
   DUEDATE_ = #{duedate, jdbcType=TIMESTAMP}

where ID_= #{id, jdbcType=VARCHAR}
  and REV_ = #{revision, jdbcType=INTEGER}

update ${prefix}CRB_RU_JOB

   REV_ =  #{revisionNext, jdbcType=INTEGER},
   LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP},
   LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR},
   RETRIES_ = #{retries, jdbcType=INTEGER},
   EXCEPTION_STACK_ID_ = #{exceptionByteArrayId, jdbcType=VARCHAR},
   EXCEPTION_MSG_ = #{exceptionMessage, jdbcType=VARCHAR}

where ID_= #{id, jdbcType=VARCHAR}
  and REV_ = #{revision, jdbcType=INTEGER}
